mapper.select:
SELECT
	COUNT(*)
FROM
	favorites

	room_id = #{roomId}
	AND member_id = #{memberId}

SELECT room_id roomId, favorites_url favoritesUrl, favorites_title favoritesTitle FROM favorites
member_id = #{memberId}
ORDER BY create_date DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.delete:
DELETE FROM favorites

	member_id = #{memberId}
	AND room_id = #{roomId}
