mapper.select:
SELECT question_id, writer_id, writer_category, question_title, secret_status, answer_status, create_date FROM question
writer_category = #{writerCategory}
ORDER BY question_id DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT question_id, writer_id, writer_category, question_title, secret_status, answer_status, create_date FROM question
answer_status = 'N'
AND writer_category = #{writerCategory}
ORDER BY question_id DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM question
answer_status = 'N'
AND writer_category = #{writerCategory}
