mapper.select:
SELECT room_id roomId, detail_address_id detailAddressId, room_category roomCategory, room_form roomForm, room_name roomName, room_intro roomIntro, min_day minDay, people_num peopleNum, room_price roomPrice FROM room
room_name LIKE CONCAT ('%', #{keyword}, '%')
ORDER BY create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT attraction_id attractionId, attraction_category attractionCategory, attraction_name attractionName, attraction_content attractionContent FROM attraction
attraction_name LIKE CONCAT ('%', #{keyword}, '%')
ORDER BY create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT r.room_id roomId, r.detail_address_id detailAddressId, r.room_category roomCategory, r.room_form roomForm, r.room_name roomName, r.room_intro roomIntro, r.min_day minDay, r.people_num peopleNum, r.room_price roomPrice FROM room r INNER JOIN hashtag h ON r.room_id = h.hashtag_target
h.hashtag_name LIKE CONCAT ('%', #{keyword}, '%')
ORDER BY r.create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT a.attraction_id attractionId, a.attraction_category attractionCategory, a.attraction_name attractionName, a.attraction_content attractionContent FROM attraction a INNER JOIN hashtag h ON a.attraction_id = h.hashtag_target
h.hashtag_name LIKE CONCAT ('%', #{keyword}, '%')
ORDER BY a.create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT room_id roomId, detail_address_id detailAddressId, room_category roomCategory, room_form roomForm, room_name roomName, room_intro roomIntro, min_day minDay, people_num peopleNum, room_price roomPrice FROM view_room_address
room_name LIKE CONCAT ('%', #{keyword}, '%') AND sido LIKE #{sido} AND sigungu LIKE #{sigungu}
ORDER BY create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT attraction_id attractionId, attraction_category attractionCategory, attraction_name attractionName, attraction_content attractionContent FROM view_attraction_address
attraction_name LIKE CONCAT ('%', #{keyword}, '%') AND sido LIKE #{sido} AND sigungu LIKE #{sigungu}
ORDER BY create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT v.room_id roomId, v.detail_address_id detailAddressId, v.room_category roomCategory, v.room_form roomForm, v.room_name roomName, v.room_intro roomIntro, v.min_day minDay, v.people_num peopleNum, v.room_price roomPrice FROM view_room_address v INNER JOIN hashtag h ON v.room_id = h.hashtag_target
h.hashtag_name LIKE CONCAT ('%', #{keyword}, '%') AND sido LIKE #{sido} AND sigungu LIKE #{sigungu}
ORDER BY v.create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT v.attraction_id attractionId, v.attraction_category attractionCategory, v.attraction_name attractionName, v.attraction_content attractionContent FROM view_attraction_address v INNER JOIN hashtag h ON v.attraction_id = h.hashtag_target
h.hashtag_name LIKE CONCAT ('%', #{keyword}, '%') AND sido LIKE #{sido} AND sigungu LIKE #{sigungu}
ORDER BY v.create_date DESC LIMIT #{beginRow}, #{rowPerPage}
