mapper.delete:
DELETE 
FROM system_admin
WHERE system_admin_id = #{systemAdminId} AND system_admin_pw = #{systemAdminPw}

mapper.select:
SELECT user_id userId, point_history_category pointHistoryCategory, point, changed_point changedPoint, create_date createDate FROM view_user_point_history
create_date BETWEEN #{minDay} AND #{maxDay}
ORDER BY create_date DESC;

SELECT 
	DATE_FORMAT(create_date, '%Y-%m') monthList, 
	SUM(fee) revenue
FROM fee

	YEAR(create_date) = #{year}

GROUP BY monthList;
